/* Inject original CSS here */

body {
  font-family: "Poppins", Helvetica, Arial, sans-serif;
  line-height: 1.6;
  color: #000000;
}

.home {
  display: flex;
  justify-content: center;
  background-color: #ffffff;
}

.content {
  max-width: 1280px;
  width: 100%;
  position: relative;
}

.navigation {
  position: sticky;
  top: 0;
  z-index: 100;
  background-color: #ffffff;
  box-shadow: 0 4px 5px rgba(0, 0, 0, 0.05);
}

.header-content {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1rem 2rem;
}

.logo {
  font-size: 24px;
  letter-spacing: 2.16px;
}

.logo-text {
  font-weight: 500;
}

.logo-highlight {
  font-weight: 700;
  color: #d1e45a;
}

.navbar ul {
  display: flex;
  list-style: none;
  gap: 2rem;
}

.navbar a {
  color: #000000;
  font-size: 14px;
  font-weight: 500;
  text-decoration: none;
  transition: color 0.3s ease;
}

.navbar a:hover,
.navbar a.active {
  color: #d1e45a;
}

.search-button {
  background: none;
  border: none;
  cursor: pointer;
  padding: 0.5rem;
}

.search-icon {
  width: 20px;
  height: 20px;
}

.hero-section {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 4rem 2rem;
}

.hero-content {
  max-width: 50%;
}

.hero-title {
  font-size: 50px;
  font-weight: 300;
  letter-spacing: 1.5px;
  margin-bottom: 2rem;
}

.hero-title span {
  display: block;
}

.hero-description {
  font-size: 14px;
  color: #404040;
  margin-bottom: 2rem;
}

.cta-button {
  display: inline-flex;
  align-items: center;
  background: linear-gradient(
    90deg,
    rgba(159, 214, 133, 1) 0%,
    rgba(249, 221, 3, 1) 100%
  );
  color: #ffffff;
  text-decoration: none;
  font-weight: 700;
  font-size: 18px;
  padding: 1rem 2rem;
  border-radius: 4px;
  transition: opacity 0.3s ease;
}

.cta-button:hover {
  opacity: 0.9;
}

.cta-icon {
  background-color: #404040;
  padding: 1rem;
  margin-right: 1rem;
  border-radius: 4px 0 0 4px;
}

.hero-image {
  max-width: 50%;
  height: auto;
}

@media (max-width: 768px) {
  .header-content {
    flex-direction: column;
    align-items: flex-start;
  }

  .navbar ul {
    flex-direction: column;
    gap: 1rem;
  }

  .hero-section {
    flex-direction: column;
  }

  .hero-content,
  .hero-image {
    max-width: 100%;
  }
}
